mapper.insert:
INSERT INTO book
( bookId
, title
, author
, price
, isbn
, publish
, moddate
)
VALUES 
( #{bookId}
, #{title}
, #{author}
, #{price}
, #{isbn}
, #{publish}
, sysdate
)

mapper.update:
UPDATE book b 			
SET b.title = #{title}		
  , b.author = #{author}		
  , b.price = #{price}	
  , b.isbn = #{isbn}		
  , b.publish = #{publish}	
  , b.moddate = sysdate 	
 WHERE b.bookid = #{bookId}

mapper.select:
SELECT b.bookid
  FROM book b
 WHERE b.bookid = #{bookId}
